mapper.select:
select 
  userid,
  username,
  password,
  salt,
  email,
  nickname,
  fullname,
  gender,
  idcard,
  domain,
  ctime,
  mtime,
  status,
  is_online
from admin_user


     userid=#{userid}


     and username=#{username}


     and password=#{password}


     and salt=#{salt}


     and email=#{email}


     and nickname=#{nickname}


     and fullname=#{fullname}


     and gender=#{gender}


     and idcard=#{idcard}


     and domain=#{domain}


     and ctime=#{ctime}


     and mtime=#{mtime}


     and status=#{status}


     and is_online=#{isOnline}

