mapper.select:
select 
  content_id,
  board_id,
  type,
  page_id,
  title,
  content,
  attachmentid,
  bind_type,
  bind_source,
  count,
  sort_order,
  status,
  start_time,
  end_time,
  ctime,
  mtime
from advert_content


     content_id=#{contentId}


     and board_id=#{boardId}


     and type=#{type}


     and page_id=#{pageId}


     and title=#{title}


     and content=#{content}


     and attachmentid=#{attachmentid}


     and bind_type=#{bindType}


     and bind_source=#{bindSource}


     and count=#{count}


     and sort_order=#{sortOrder}


     and status=#{status}


     and start_time=#{startTime}


     and end_time=#{endTime}


     and ctime=#{ctime}


     and mtime=#{mtime}

