mapper.delete:
delete from 
  bts_order_item
where 
 item_id=#{itemId} 
 and order_id=#{orderId}

mapper.select:
select 
  item_id,
  order_id,
  content_id,
  sku,
  goods_name,
  goods_image,
  goods_weight,
  goods_length,
  goods_wide,
  goods_height,
  description,
  buy_price,
  buy_num,
  is_comment,
  is_package,
  ctime
from bts_order_item


     item_id=#{itemId}


     and order_id=#{orderId}


     and content_id=#{contentId}


     and sku=#{sku}


     and goods_name=#{goodsName}


     and goods_image=#{goodsImage}


     and goods_weight=#{goodsWeight}


     and goods_length=#{goodsLength}


     and goods_wide=#{goodsWide}


     and goods_height=#{goodsHeight}


     and description=#{description}


     and buy_price=#{buyPrice}


     and buy_num=#{buyNum}


     and is_comment=#{isComment}


     and is_package=#{isPackage}


     and ctime=#{ctime}

select 
  item_id,
