  nickname,
  realname,
  idcard,
  gender,
  avatar,
  device_type,
  device_no,
  channelid,
  status,
  is_verify,
  is_del,
  last_login_time,
  last_login_ip,
  ban_etime,
  ctime,
  mtime
from cas_user


     userid=#{userid}


     and user_group_id=#{userGroupId}


     and user_lv_id=#{userLvId}


     and username=#{username}


     and password=#{password}


     and freeze=#{freeze}


     and thirdid=#{thirdid}


     and salt=#{salt}


     and encrypt=#{encrypt}


     and tel=#{tel}


