mapper.delete:
delete from 
  comment_attachment 
where 
 comment_id=#{commentId} 
 and attachmentid=#{attachmentid}

mapper.select:
select 
  comment_id,
  attachmentid,
  type,
  userid
from comment_attachment


     comment_id=#{commentId}


     and attachmentid=#{attachmentid}


     and type=#{type}


     and userid=#{userid}

