mapper.select:
select 
  coupon_id,
  coupon_name,
  total,
  face_value,
  status,
  coupon_type,
  coupon_point,
  is_verify,
  disabled,
  is_exchange,
  rule,
  body,
  valid_stime,
  valid_etime,
  grant_stime,
  grant_etime,
  is_del,
  ctime,
  mtime
from coupon_category


     coupon_id=#{couponId}


     and coupon_name=#{couponName}


     and total=#{total}


     and face_value=#{faceValue}


     and status=#{status}


     and coupon_type=#{couponType}


     and coupon_point=#{couponPoint}


     and is_verify=#{isVerify}


     and disabled=#{disabled}


     and is_exchange=#{isExchange}


     and rule=#{rule}


     and body=#{body}


     and valid_stime=#{validStime}


     and valid_etime=#{validEtime}


     and grant_stime=#{grantStime}


     and grant_etime=#{grantEtime}


     and is_del=#{isDel}


     and ctime=#{ctime}


     and mtime=#{mtime}

select 
