mapper.sql:
	   from coupon_category


            (
                coupon_name LIKE CONCAT(CONCAT('%', #{key}), '%')
                or body LIKE CONCAT(CONCAT('%', #{key}), '%')
                or rule LIKE CONCAT(CONCAT('%', #{key}), '%')
            )


	        and status=#{status}


	        and coupon_type=#{type}


	        and is_exchange=#{isExchange}


	        and valid_stime>=#{validStartDate}


	        and valid_etime<=#{validEndDate}


	        and grant_stime>=#{grantStartDate}


	        and grant_etime<=#{grantEndDate}
