mapper.select:
select 
  coupon_id,
  basic_price,
  relation_type,
  relation_content
from coupon_relation


     coupon_id=#{couponId}


     and basic_price=#{basicPrice}


     and relation_type=#{relationType}


     and relation_content=#{relationContent}

