mapper.select:
	   select 
         sku_id,
         content_id,
         sku,
         stock,
         weight,
         length,
         wide,
         height,
         price,
         price_market,
         price_cost,
         property,
         sales_volume,
         sales_amount,
         rank_average,
         viewed,
         rev,
         sort_order,
         attachmentids,
         is_shelf,
         is_del,
         ctime,
         mtime
       from goods_content_sku


            sku_id=#{skuId}


            and content_id=#{contentId}


            and sku=#{sku}


            and stock=#{stock}


            and weight=#{weight}


            and length=#{length}


            and wide=#{wide}


            and height=#{height}


            and price=#{price}


            and price_market=#{priceMarket}


            and price_cost=#{priceCost}


            and property=#{property}


            and sales_volume=#{salesVolume}


            and sales_amount=#{salesAmount}


            and rank_average=#{rankAverage}


            and viewed=#{viewed}


            and rev=#{rev}


            and sort_order=#{sortOrder}


            and attachmentids=#{attachmentids}


            and is_shelf=#{isShelf}


            and is_del=#{isDel}


            and ctime=#{ctime}


            and mtime=#{mtime}

