mapper.select:
select 
  bulk_id,
  title,
  category_id,
  start_time,
  end_time,
  inventory_sum,
  inventory,
  price,
  sku,
  goods_name,
  integral,
  buynumber,
  ctime
from groupon_grab


     bulk_id=#{bulkId}


     and title=#{title}


     and category_id=#{categoryId}


     and start_time=#{startTime}


     and end_time=#{endTime}


     and inventory_sum=#{inventorySum}


     and inventory=#{inventory}


     and price=#{price}


     and sku=#{sku}


     and goods_name=#{goodsName}


     and integral=#{integral}


     and buynumber=#{buynumber}


     and ctime=#{ctime}

select 
