        FROM
            store_content s
        LEFT JOIN cas_user u ON s.userid = u.userid
		LEFT JOIN trend_region r ON s.region_id = r.region_id
		LEFT JOIN trend_region r2 ON r.pid = r2.region_id
		LEFT JOIN trend_region r3 ON r2.pid = r3.region_id


