mapper.select:
select 
  property_id,
  name,
  val_inputtype,
  label_name,
  placeholder,
  note,
  sort_order,
  is_spec,
  status
from trend_property


     property_id=#{propertyId}


     and name=#{name}


     and val_inputtype=#{valInputtype}


     and label_name=#{labelName}


     and placeholder=#{placeholder}


     and note=#{note}


     and sort_order=#{sortOrder}


     and is_spec=#{isSpec}


     and status=#{status}

