mapper.sql:
	   from trend_property p,trend_property_value v

             p.property_id = v.property_id

             and p.label_name LIKE CONCAT(CONCAT('%', #{key}), '%')


            and p.property_id=#{propertyId}


            and p.name=#{name}


            and p.val_inputtype=#{valInputtype}


            and p.label_name=#{labelName}


            and p.placeholder=#{placeholder}


            and p.note=#{note}


            and p.sort_order=#{sortOrder}


            and p.is_spec=#{isSpec}


            and p.status=#{status}


       group by 
                v.property_id

mapper.select:
SELECT p.property_id, p.name, p.val_inputtype, p.label_name, p.placeholder, p.note, p.sort_order, p.is_spec, p.status, v.property_value_id, v.property_value, v.property_image, v.sort_order AS value_sort_order, v.is_default, v.status AS value_status FROM goods_property_category pc, trend_property_to_group tog, trend_property p, trend_property_value v
pc.property_group_id = tog.property_group_id AND tog.property_id = p.property_id AND p.property_id = v.property_id OR pc.property_id = p.property_id AND p.property_id = v.property_id AND pc.category_id = #{categoryId}
GROUP BY v.property_value_id ORDER BY p.sort_order,v.sort_order

SELECT p.property_id, p.name, p.val_inputtype, p.label_name, p.placeholder, p.note, p.sort_order, p.is_spec, p.status, v.property_value_id, v.property_value, v.property_image, v.sort_order AS value_sort_order, v.is_default, v.status AS value_status FROM trend_property p, trend_property_value v, goods_property g
p.property_id = v.property_id AND g.property_id = p.property_id AND g.property_value_id = v.property_value_id AND g.content_id = #{contentId}
ORDER BY p.sort_order,v.sort_order
