mapper.select:
select 
  region_id,
  pack,
  pid,
  hid,
  grade,
  region_name,
  en_name,
  p_1,
  p_2,
  sort_order,
  status
from trend_region


     region_id=#{regionId}


     and pack=#{pack}


     and pid=#{pid}


     and hid=#{hid}


     and grade=#{grade}


     and region_name=#{regionName}


     and en_name=#{enName}


     and p_1=#{p1}


     and p_2=#{p2}


     and sort_order=#{sortOrder}


     and status=#{status}

